import xml.etree.cElementTree as ET                         # to parse XML files
import nltk as nl                                           # to determine parts of speech
from sklearn.linear_model import LogisticRegression         # to apply logistic regression
from sklearn.model_selection import cross_val_score         # to find out the accuracy of logistic regression

trainingFeatures = []   # [[prepNumber, pronounNumber, areWomenWordsUsed, areMenWordsUsed]..[..]]
target = []             # 1: female, 0: male
tweets = []

def readAndParseDatas():

    fp = open("truth.txt","r")
    personID = []

    for line in fp:
        personID.append(line.split(":::"))  # add person tag, gender and country to personID array

    for i in range(len(personID)):
        tweets.append([])   # create 2d array whose size is the same as personID array

    for i in range(len(personID)):

        id = personID[i][0]    # personID=[person tag - gender - country]
        gender = personID[i][1]

        tree = ET.ElementTree(file = id + '.xml')
        root = tree.getroot()

        # tweets array has format [personTag-personGender-personTweets] for each person
        tweets[i].append(id)
        tweets[i].append(gender)

        # parse xml files and add tweets in tweets array for each person
        for documents in root:
            for document in documents:
                if(document.tag == 'document'):
                    tweets[i].append(document.text)

def addPrepAndpronounFeatures():

    for i in range(0,len(tweets)):

        #initializing the preposition and pronoun numbers
        pronounNum = 0
        prepNum = 0

        print("Remaining iteration: ", 3600-i)

        toksents = []    # tokenized tweets
        sents = []       # person tweets

        for j in range(2,102):

            sents.append(tweets[i][j])    # add all tweets of person in an array

        for s in sents:
                toksents.append(nl.word_tokenize(s))      # tokenize all the tweets and add them to an array

        arr = nl.pos_tag_sents(toksents)     # determine the part of speeches of each word that person used

        # when i call nltk.pos_tag_sents() for every individual tweets, it took about 3 hours to complete
        # but in this way, this function is called only when all tweets of a person's has been tokenized
        # and it only takes about 10 minutes to complete

        for x in range(0,len(arr)):

            for y in range(0,len(arr[x])):

                # PRP: personal pronoun, PRP$: possesive pronoun, WP: wh-pronoun, WP$: possesive wh-pronoun

                if (arr[x][y][1] == 'PRP' or arr[x][y][1] == 'PRP$' or arr[x][y][1] == 'WP' or arr[x][y][1] == 'WP$'):
                    pronounNum += 1

                if (arr[x][y][1] == 'IN'):
                    prepNum += 1

        trainingFeatures.append([pronounNum, prepNum])

        constructTargetArr(i)  # while creating features for a person, target array( female or male) is created as well

def addMostCommonWords():

    for i in range(0,len(tweets)):

        flagFoundWomen = False  # whether person used one of the most common women words or not
        flagFoundMen = False    # whether person used one of the most common men words or not

        for j in range(2,102):

            while(not flagFoundWomen):

                if ('heart' in tweets[i][j] or 'beautiful' in tweets[i][j] or 'excited' in tweets[i][j]):  # some of the most common words observed that are used by women
                    if(flagFoundMen): 
                        trainingFeatures[i][2] = 1
                    else:
                        trainingFeatures[i].append(1)
                        trainingFeatures[i].append(0) #flagFoundMen is still false
                    flagFoundWomen = True

                else:
                    break

            while (not flagFoundMen):

                if ('mate' in tweets[i][j] or 'player' in tweets[i][j]):   # some of the most common words observed that are used by men
                    if(flagFoundWomen):
                        trainingFeatures[i][3] = 1
                    else:
                        trainingFeatures[i].append(0) #flagFoundWomen is still false
                        trainingFeatures[i].append(1)
                    flagFoundMen = True
                else:
                    break
            # in last iteration, none of the words have been found, then both features are 0
            if (j == 101 and flagFoundMen == False and flagFoundWomen == False):

                trainingFeatures[i].append(0)
                trainingFeatures[i].append(0)
            #both of them found, there is no need to continue to execute loop
            if(flagFoundMen and flagFoundWomen):
                break


def constructTargetArr(i):

    if (tweets[i][1] == 'female'):
        target.append(1)         # female: 1
    elif (tweets[i][1] == 'male'):
        target.append(0)         # male: 0

def fitDatasAndCalculateAccuracy():

    accuracy = cross_val_score(LogisticRegression(), trainingFeatures, target, scoring='accuracy', cv=10).mean() * 100
    print("Success: %", accuracy)

def main():

    readAndParseDatas()
    addPrepAndpronounFeatures()
    addMostCommonWords()
    fitDatasAndCalculateAccuracy()

main()

